import { useState } from "react";
import weaponsList from '../assets/weapons.json';
import WeaponCard from "./WeaponCard";
import FilterForm from "./FilterForm";
import PageHeader from "./PageHeader";

const lodash = require("lodash");

export default function Weapons() {

  // mandatory filters that will always have a value of some sort
  const [filters, setFilters] = useState({
    power_type: [],
    min_power: 0,
    max_power: 15000,
    min_power_bonus: 0,
    max_power_bonus: 1,
    min_attr_bonus: 0,
    max_attr_bonus: 1,
    min_luck: 0,
    max_luck: 500,
    min_title_req: 0,
    max_title_req: 19,
    min_cheese_effect: 0,
    max_cheese_effect: 12,
  })

  // Filter the first list further with the manatory filters (not lodash this time)
  let filteredList = weaponsList.filter(x =>
    (x.power_type == filters.power_type || !x.power_type) &&
    x.power >= filters.min_power &&
    x.power <= filters.max_power &&
    x.power_bonus >= filters.min_power_bonus &&
    x.power_bonus <= filters.max_power_bonus &&
    x.attr_bonus >= filters.min_attr_bonus &&
    x.attr_bonus <= filters.max_attr_bonus &&
    x.luck >= filters.min_luck &&
    x.luck <= filters.max_luck &&
    x.title_req >= filters.min_title_req &&
    x.title_req <= filters.max_title_req &&
    x.cheese_effect >= filters.min_cheese_effect &&
    x.cheese_effect <= filters.max_cheese_effect &&
    x.limited == filters.limited
  );

  // create a sort (can take multiple columns)
  // TO DO: find out how to change sort direction
  const [currentSort, setCurrentSort] = useState(["title_req"])

  let sortedAndFilteredList = lodash.sortBy(filteredList, currentSort);

  // return the page
  return (
    <>

      <PageHeader />

      <FilterForm
        
        childSetMandatory={setfilters}
        childSetOptional={setOptionalFilters}
        childSetSort={setCurrentSort}
      />

      <div className="card-container">
        {sortedAndFilteredList.map((weapon) => (
          <WeaponCard key={weapon.name} weapon={weapon} />
        ))}
      </div>

    </>

  );

}
