import { useEffect, useState } from "react";
import weaponsList from '../assets/weapons.json';
import typesList from '../assets/types.json';

export default function Weapons() {


    const [typeFilter, setTypeFilter] = useState(['Arcane']);

    const filteredList = weaponsList.filter((weapon) => {
      return typeFilter.length ? typeFilter.includes(weapon.power_type) : true;
    });

    const handleCheckboxClick = (e) => {
      if(!typeFilter.includes(e.target.value)) {
        setTypeFilter([...typeFilter, e.target.value]);
      } else {
        const newTypeFilter = typeFilter.filter(oldType => {
          return oldType != e.target.value;
        })
      }
    }

    return(
      <>
        <form style={{margin: '1rem'}}>
            {/* <select onChange={(e) => setType(e.target.value)}>
              <option value="">Select a Type</option>
              {typesList.map((type) => (
                <option>{type}</option>
              ))}
            </select> */}

            {typesList.map(type => (
              <>
                <input 
                  type="checkbox" 
                  name={type} 
                  value={type} 
                  checked={typeFilter.includes(type)} 
                  onClick={handleCheckboxClick}
                />
                <label htmlFor={type}>{type}</label>
              </>
            ))}
        </form>

        {filteredList.map((weapon) => (
          <div key={weapon.id}>
            {weapon.name} - {weapon.power} - {weapon.title_req}
          </div>
        ))}
      </>
    );
}
